    const matchingControl = fg.controls[matchingControlName];

    if(!passwordControl.touched && !matchingControl.touched) {
      return null;
    }

    if(passwordControl.value !== matchingControl.value) {
      return {[MATCHING_ERROR_KEY]: true};
    }

    return null;
  };
}

